const Reducer_80 = (state, action, payload) => {
    if (action.type === 'CLEAR_CART') {
        return { ...state, cart: [], amount: 0, total: 0 }
    }

    if (action.type === 'INCREASE') {
        return {...state, }
    }

    if (action.type === 'REMOVE_CART') {
        return { ...state, amount: -1}
    }
}

export default Reducer_80;
